drop user DEMO cascade;
create user DEMO identified by Welcome_12345;
grant DWROLE to DEMO;
grant unlimited TABLESPACE to DEMO;

CREATE TABLE "DEMO"."DEPARTMENTS" (
	"DEPARTMENT_ID" NUMBER(4,0) NOT NULL PRIMARY KEY,
	"DEPARTMENT_NAME" VARCHAR2(30),
	"MANAGER_ID" NUMBER(6,0),
	"LOCATION_ID" NUMBER(4,0)
);

commit;

CREATE TABLE "DEMO"."EMPLOYEES" (
	"EMPLOYEE_ID" NUMBER(6,0) NOT NULL PRIMARY KEY,
	"FIRST_NAME" VARCHAR2(20),
	"LAST_NAME" VARCHAR2(25),
	"EMAIL" VARCHAR2(25),
	"PHONE_NUMBER" VARCHAR2(20),
	"HIRE_DATE" DATE,
	"JOB_ID" VARCHAR2(10),
	"SALARY" NUMBER(8,2),
	"CARD_NO" NUMBER(16,0),
	"MANAGER_ID" NUMBER(6,0),
	"DEPARTMENT_ID" NUMBER(4,0),
	CONSTRAINT FK_DEP_EMP FOREIGN KEY (DEPARTMENT_ID)
	REFERENCES DEMO.DEPARTMENTS(DEPARTMENT_ID)
);

commit;
